import React, { Component } from 'react';
import { View, Text,StyleSheet,TouchableOpacity,Image,FlatList} from 'react-native';
import { SearchBar,Button, ThemeConsumer} from 'react-native-elements';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      fullData:[],
      loading:false,
      error:null
    };
  }
  componentDidMount(){
    this.getData()
  } 
  getData =  () =>{
    this.setState({loading:true})
    const endPoint = 'http://2tor-pruebas.eba-39fqbkdu.us-west-1.elasticbeanstalk.com/auth/busqueda/'

    let collection = {}
    collection.busqueda = 'matematicas'
    
    fetch(endPoint, {
      method: 'POST', // or 'PUT'
      body: JSON.stringify(collection), // data can be `string` or {object}!
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => res.json())
    .then((resJson)=>{
      this.setState({
        loading:false,
        data: resJson.datos,
        fullData:resJson.datos
      })
    }).catch(error=>{
      this.setState({error,loading:false})
    })
    console.log('x:'+this.state.data)
    
  }
  _renderItem = ({item,index}) =>{
    console.log(item)
    return (
      <TouchableOpacity style={styles.card}>
          <Image style={styles.cardImage} source={{uri:'https://concepto.de/wp-content/uploads/2015/03/paisaje-e1549600034372.jpg'}}/>
    <Text style={styles.cardText}>{item}</Text>
        </TouchableOpacity>
    )
  } 
  render() {
    let {items} = this.state
    return (
        <View style={styles.container}>
        <FlatList style={styles.container} data={this.state.data} keyExtractor={(item,index)=>index.toString()} renderItem={this._renderItem}/>
        </View>
      
    );
  }
}
const styles = StyleSheet.create({

    container:{
        width:'100%',
        height:'100%',
        flex:1,
       
    },
    search:{
        backgroundColor:'white'
    },
    textList:{
        padding:25,
        fontSize:15,
        color:'black',
        
    },
    card:{
      backgroundColor:'#fff',
      marginBottom:10,
      marginLeft:'2%',
      width:'96%',
      shadowColor:'#000',
      shadowOpacity:1,
      shadowOffset:{
        width:3,
        height:3
      }
    },
    cardText:{
      fontSize:25,
      padding:10
    },
    cardImage:{
      width:'100%',
      height:200,
      resizeMode:'cover'
    }
})
