import * as React from 'react';
import { Text,TextInput,View, Image,ImageBackground, StyleSheet } from 'react-native';
import { Icon,Button,Card } from 'react-native-elements'
const imgbg = require('../assets/ex.jpg');
export default class LoginScreen extends React.Component {
  constructor(props){
    super(props)
    /*const token = localStorage.getItem("token")

    
    if(token==null){
        loggedIn = false
    }*/
    let loggedIn = true
    this.state = {
        email:'',
        password:'',
        loggedIn
    }
}
  static navigationOptions ={
    header: null
  }


changeHandler = e =>{
  this.setState({[e.target.name]:e.target.value})
}
submit(){
let collection={}
collection.email=this.state.email,
collection.password=this.state.password

console.log(collection)

var url = 'http://127.0.0.1:8000/auth/login/';

fetch(url, {
  method: 'POST', // or 'PUT'
  body: JSON.stringify(collection), // data can be `string` or {object}!
  headers:{
    'Content-Type': 'application/json'
  }
}).then(res => res.json())
.catch(error => console.error('Error:', error))
.then(response => console.log('Success:', response.email));


}

  render() {
    
    const {email, password, loggedIn} = this.state;
    
      
     
    return (
      
        <ImageBackground  style={{width: '100%', height: '100%'}}>
          
          <View style={styles.container}>
             
            

            <View style={styles.containerUserName}>
              <Icon type="font-awesome" name="user" color="gray" containerStyle={styles.icon}/>
              <TextInput placeholder="Email" placeholderTextColor="gray"
              style={styles.textInput} onChange={this.changeHandler}name="email" value={email}  /> 
            </View>

            <View style={styles.containerPassword}>
              <Icon type="entypo" name="key" color="gray" containerStyle={styles.icon}/>
              <TextInput placeholder="Contrasena" placeholderTextColor="gray"
              style={styles.textInput} onChange={this.changeHandler} name="password"value={password} secureTextEntry={true} /> 
            </View>

            <View style={styles.containerSignIn}>
              <Button title='Iniciar Sesion' backgroundColor='red' onPress={()=>this.submit()}/>
            </View>
            
            <View style={styles.containerRegister}> 
              <Text style={{color:'white',fontWeight: 'bold'}}>No tienes cuenta aun?
                <Text  onPress={() => this.props.navigation.navigate('Register')}
                style={{color:'red',fontWeight: 'bold'}}>  Registrate!</Text>
              </Text>
            </View>
            
          </View>        
        </ImageBackground>
    );
  }
}

const styles = StyleSheet.create({
  container:{
    flex:1,
    flexDirection: 'column',
    justifyContent:'center',
    alignItems: 'stretch',
  },
  containerSignIn:{
    height: 60,
    width:'30%',
    marginTop:'10px',
    alignSelf:'center',

  },
  
  containerUserName:{
    height: 60,
    flexDirection:'row',
    justifyContent:'center',
    backgroundColor:'#ffffff',
    marginLeft:'10%',
    marginRight:'10%',
  },
  containerPassword:{
    height: 60,
    flexDirection:'row',
    justifyContent:'center',
    backgroundColor:'#ffffff',
    marginLeft:'10%',
    marginRight:'10%',
  },
  containerRegister:{
    height: 60,
    marginLeft:'6%',
    marginRight:'6%',
    marginTop:'20px',
    alignItems: 'center',
  },
  icon:{
    flex:1
  },
  textInput:{
    backgroundColor:'transparent',
    flex:5,
    color:'black',
    paddingLeft:'25%'
  }
})
