import * as React from 'react';
import { Text, TextInput, View, ImageBackground, StyleSheet, TouchableOpacity } from 'react-native';
import { Icon, Button } from 'react-native-elements'
import RegisterUsScreen from '../screens/RegistroUs'
import RegisterProfScreen from '../screens/RegistroProf'
const imgFooter = require('../assets/reg.jpg');

export default class RegisterScreen extends React.Component {
  constructor(props) {
    super(props)


    this.state = {
      cardstate: 0,
      isFocused: false
    }
  }
  onFocusChange = () => {
    this.setState({ isFocused: true });
  }

  static navigationOptions = {
    header: null
  }

  render() {

    return (
      <View style={styles.container}>

        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={(this.state.cardstate==1) ? styles.buttonUs : styles.buttonUsF} onPress={() => this.setState({ cardstate: 0 })}>
            <Text style={styles.textButton}>Registro Alumnos</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={(this.state.cardstate==0) ? styles.buttonTut : styles.buttonTutF} onPress={() => this.setState({ cardstate: 1 })}>
            <Text style={styles.textButton}>Registro Tutores</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.tabsContainer}>
          {this.state.cardstate == 0 ? (<RegisterUsScreen />) : (<RegisterProfScreen />)}

        </View>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {

    width: '100%',
    height: '100%'
  },
  buttonUs: {
    alignItems: "center",
    padding: 10,
    width: '45%',
    marginLeft: '-15%',
  },
  buttonUsF: {
    alignItems: "center",
    padding: 10,
    width: '45%',
    marginLeft: '-15%',
    borderBottomWidth: '1px',
    borderBottomColor: '#22d48a'
  },
  buttonTut: {
    alignItems: "center",
    padding: 10,
    width: '45%',
    marginLeft: '40%',
    

  },
  buttonTutF: {
    alignItems: "center",
    padding: 10,
    width: '45%',
    marginLeft: '40%',
    borderBottomWidth: '1px',
    borderBottomColor: '#22d48a'

  },

  buttonContainer: {
    alignSelf: 'center',
    width: '50%',
    flexDirection: 'row',
    justifyContent: 'space-between',


  },
  tabsContainer: {
    height: '70%',
    width: '80%',
    alignSelf: 'center',
    backgroundColor: '#e1e9f5',
    borderRadius: '30px',
    backgroundColor: 'white'

  },
  avatar: {
    height: '120px',
    width: '120px',
    alignSelf: 'center',
    marginBottom: '15%',
    marginTop: '15%'
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10
  },
  textButton: {
    color: 'black',
    fontSize: '5px',
    fontWeight: 'bold',
  
  }
})
